function themeNameFromFile(){
	var links = document.getElementsByTagName("link");
	for (var i = 0; i < links.length; i++) {
		var res = links[i].href.match("dhtmlxscheduler_([a-z]+).css");
		if (res){
			return res[1];
		}
	}
