				if(showNowTime){
					if(timeline.x_unit === "day"){
						nowTime.setHours(0);
						nowTime.setMinutes(0);
					}else if(timeline.x_unit === "hour"){
						nowTime.setMinutes(0);
					}
					from = nowTime;
				}else{
