					from = nowTime;
				}
				from = this.findVisibleColumn(from);
				if(!showNowTime){
					from.setHours(scheduler.config.first_hour);
				}

				if(!scheduler._table_view){
					var dataContainer = scheduler.$container.querySelector(".dhx_cal_data");
					if(dataContainer.scrollTop){
