	(function() {
        var _cloneObj = function(obj){
            if(obj === null || typeof(obj) != 'object')
                return obj;
            var temp = new obj.constructor();
            for(var key in obj)
                temp[key] = _cloneObj(obj[key]);
            return temp;
        };

		scheduler._dataprocessors = [];
